import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [blague, setBlague] = useState('');
  const [error, setError] = useState('');
  const [userChoice, setUserChoice] = useState('Programming'); // Par défaut sur 'Programming'

  const handleClick = (value) => {
    setUserChoice(value);
  };

  const fetchBlague = async () => {
    try {
      const response = await fetch(`https://v2.jokeapi.dev/joke/${userChoice}?type=single&amount=1`);
      const data = await response.json();

      if (data.type === 'single') {
        setBlague(data.joke);
      } else {
        setBlague('Aucune blague trouvée');
      }
    } catch (error) {
      setError('Erreur lors de la récupération de la blague');
    }
  };

  useEffect(() => {
    fetchBlague();
  }, [userChoice]); // Re-fetch la blague à chaque changement de `userChoice`

  return (
    <div className='container'>
      <h1>Joke App</h1>
      <div className='mainContainer'>
        <h2>What kind of joke would you like to hear?</h2>
        <div className='buttonGroup'>
          <button onClick={() => handleClick('Programming')}>Programming</button>
          <button onClick={() => handleClick('Dark')}>Dark</button>
          <button onClick={() => handleClick('Any')}>Anything</button>
        </div>
        {error ? <p>{error}</p> : <p>{blague}</p>}
        <button onClick={fetchBlague}>
          Again !
        </button>
      </div>
    </div>
  );

}

export default App;
