import React, { useState, useEffect } from 'react';

function App() {
  const [blague, setBlague] = useState('');  
  const [error, setError] = useState('');    

  const [userChoice, setUserChoice] = useState(0);

  const handleClick = (value) => {
    setUserChoice(value);
  };





  const fetchBlague = async () => {
    try {
      const response = await fetch('https://v2.jokeapi.dev/joke/Programming?type=single&amount=1'); // Requête à l'API
      const data = await response.json();

      if (data.type === 'single') {
        setBlague(data.joke); // Mettre à jour l'état avec la nouvelle blague
      } else {
        setBlague('Aucune blague trouvée');
      }
    } catch (error) {
      setError('Erreur lors de la récupération de la blague');
    }
  };

  useEffect(() => {
    fetchBlague(); 
  }, []);
  return (
    <div>
      <h1>Joke App</h1>
      <h2>What kind of joke would you like to hear ?</h2>
      <button onClick={() => handleClick(1)}>Programming</button>
      <button onClick={() => handleClick(2)}>Dark</button>
      <button onClick={() => handleClick(3)}>Anything</button>






      {error ? <p>{error}</p> : <p>{blague}</p>}
      <button onClick={fetchBlague}>
        Change la Blague
      </button>
    </div>
  );
}

export default App;
