import React, { useState, useEffect } from 'react';

function App() {
  const [blague, setBlague] = useState('');  // Renommage de 'joke' à 'blague'
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchBlague = async () => {
      try {
        const response = await fetch('https://v2.jokeapi.dev/joke/Programming?type=single&amount=1'); // Correction de l'URL
        const data = await response.json();

        if (data.type === 'single') {
          setBlague(data.joke); // Correction : utilisation de 'data.joke'
        } else {
          setBlague('Aucune blague trouvée');
        }
      } catch (error) {
        setError('Erreur lors de la récupération de la blague');
      }
    };

    fetchBlague();
  }, []); // Le tableau vide indique que cet effet doit s'exécuter une seule fois

  return (
    <>
      <h1>Joke of the Day</h1>
      {error ? <p>{error}</p> : <p>{blague}</p>} {/* Correction : 'joke' remplacé par 'blague' */}
    </>
  );
}

export default App;
